DEFINE TABLE OVERWRITE devices SCHEMALESS;

# DEFINE FIELD OVERWRITE field ON devices;
DEFINE FIELD OVERWRITE id ON devices TYPE uuid DEFAULT rand::uuid::v4();
DEFINE FIELD OVERWRITE user_id ON devices TYPE string;
DEFINE FIELD OVERWRITE device ON devices TYPE string;
DEFINE FIELD OVERWRITE ip ON devices TYPE string;
DEFINE FIELD OVERWRITE last_login_at ON devices TYPE datetime DEFAULT time::now();

DEFINE INDEX device_user_id_index ON TABLE devices COLUMNS user_id;

DEFINE TABLE OVERWRITE email_verification_tokens SCHEMALESS;

# DEFINE FIELD OVERWRITE field ON email_verification_tokens;
DEFINE FIELD OVERWRITE id ON email_verification_tokens Type uuid DEFAULT rand::uuid::v4();
DEFINE FIELD OVERWRITE user_id ON email_verification_tokens Type string;
DEFINE FIELD OVERWRITE token ON email_verification_tokens Type string;
DEFINE FIELD OVERWRITE token_Type ON email_verification_tokens Type string;
DEFINE FIELD OVERWRITE created_at ON email_verification_tokens Type datetime DEFAULT time::now() READONLY;
DEFINE FIELD OVERWRITE expires_at ON email_verification_tokens Type datetime DEFAULT time::now();
DEFINE FIELD OVERWRITE used ON email_verification_tokens Type bool DEFAULT false;

DEFINE INDEX email_verification_token_index ON TABLE email_verification_tokens COLUMNS token UNIQUE;

DEFINE TABLE OVERWRITE ip_blacklist SCHEMALESS;

# DEFINE FIELD OVERWRITE field ON ip_blacklist;
DEFINE TABLE OVERWRITE refresh_tokens SCHEMALESS;

# DEFINE FIELD OVERWRITE field ON refresh_tokens;
DEFINE FIELD OVERWRITE id ON refresh_tokens TYPE uuid DEFAULT rand::uuid::v4();
DEFINE FIELD OVERWRITE user_id ON refresh_tokens TYPE string;
DEFINE FIELD OVERWRITE token_value ON refresh_tokens TYPE string;
DEFINE FIELD OVERWRITE is_revoked ON refresh_tokens TYPE bool;
DEFINE FIELD OVERWRITE created_at ON refresh_tokens TYPE datetime DEFAULT time::now() READONLY;
DEFINE FIELD OVERWRITE expired_at ON refresh_tokens TYPE datetime DEFAULT time::now();

DEFINE INDEX refresh_token_user_id_index ON TABLE refresh_tokens COLUMNS user_id;
DEFINE INDEX refresh_token_value_index ON TABLE refresh_tokens COLUMNS token_value UNIQUE;

DEFINE TABLE OVERWRITE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD OVERWRITE script_name ON script_migration TYPE string;
DEFINE FIELD OVERWRITE executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE FIELD OVERWRITE checksum ON script_migration TYPE option<string>;

DEFINE TABLE OVERWRITE users SCHEMALESS;

# DEFINE FIELD OVERWRITE field ON users;
DEFINE FIELD OVERWRITE id ON users TYPE uuid DEFAULT rand::uuid::v4();
DEFINE FIELD OVERWRITE name ON users TYPE string;
DEFINE FIELD OVERWRITE email ON users TYPE string;
DEFINE FIELD OVERWRITE password ON users TYPE string;
DEFINE FIELD OVERWRITE role ON users TYPE string;
DEFINE FIELD OVERWRITE salt ON users TYPE string;
DEFINE FIELD OVERWRITE is_verified ON users TYPE bool DEFAULT false;
DEFINE FIELD OVERWRITE status ON users TYPE string;
DEFINE FIELD OVERWRITE created_at ON users TYPE datetime DEFAULT time::now() READONLY;
DEFINE FIELD OVERWRITE updated_at ON users TYPE datetime DEFAULT time::now();

DEFINE INDEX user_email_index ON TABLE users COLUMNS email UNIQUE;
